import React, { useState } from 'react'
import Game from './game'

export default function App (){
    const [point,setPoint]=useState(0)
    const [highPoint,setHighPoint]=useState(0)
    const [winState,setWinState]=useState(false)

    function calculatePoint(clicked){
        if(!clicked){
            //i have to create a new point variable because the state hook doesn't update immediately
            let newpoint=point+1
            setPoint(newpoint)
            if(newpoint===10){
                setWinState(true)
                setHighPoint(highPoint=>(highPoint<point)?point:highPoint)
            }

            return
        }

        setHighPoint(highPoint=>(highPoint<point)?point:highPoint)
        setPoint(0)

        return
    }

    function restart(){
        setPoint(0)
        setWinState(false)
    }


    return(
        <div>
            <p>{point}</p>
            <p>{highPoint}</p>
            <Game calculatePoint={calculatePoint} restart={restart} winState={winState}></Game>
        </div>
    )
}
