import React, { useState } from 'react'
import Game from './game'

export default function App (){
    let [point,setPoint]=useState(0)
    let [winState,setWinState]=useState(false)

    function calculatePoint(clicked){
        (!clicked)?setPoint(point+=1):setPoint(0)
        if(point===10) console.log('you win')
    }

    function restart(){
        setPoint(0)
        setWinState(false)
    }


    return(
        <div>
            <p>{point}</p>
            <Game calculatePoint={calculatePoint} restart={restart} winState={winState}></Game>
        </div>
    )
}
