import React, { useState } from 'react'

export default function Game(props){
    const [data,setData]=useState([
        {
            name:'Card 1',
            clicked:false
        },
        {
            name:'Card 2',
            clicked:false
        },
        {
            name:'Card 3',
            clicked:false
        },
        {
            name:'Card 4',
            clicked:false
        },
        {
            name:'Card 5',
            clicked:false
        },
        {
            name:'Card 6',
            clicked:false
        },
        {
            name:'Card 7',
            clicked:false
        },
        {
            name:'Card 8',
            clicked:false
        },
        {
            name:'Card 9',
            clicked:false
        },
        {
            name:'Card 10',
            clicked:false
        },
    ]
    )

    function resetData(){
        setData([
            {
                name:'Card 1',
                clicked:false
            },
            {
                name:'Card 2',
                clicked:false
            },
            {
                name:'Card 3',
                clicked:false
            },
            {
                name:'Card 4',
                clicked:false
            },
            {
                name:'Card 5',
                clicked:false
            },
            {
                name:'Card 6',
                clicked:false
            },
            {
                name:'Card 7',
                clicked:false
            },
            {
                name:'Card 8',
                clicked:false
            },
            {
                name:'Card 9',
                clicked:false
            },
            {
                name:'Card 10',
                clicked:false
            },
        ])
    }

    function randomSort(){
        let clone=data

        for(let i=clone.length-1;i>0;i--){
            const j =Math.floor(Math.random()*(i+1))
            const temp=clone[i]
            clone[i]=clone[j]
            clone[j]=temp
        }

        setData(clone)
    }

    function handleClick(e){
        let index=Number(e.target.getAttribute('name'))

        let clone=data
        if (clone[index].clicked){
            resetData()
            randomSort()
            props.calculatePoint(clone[index].clicked)

            return
        }
        clone[index].clicked=true

        randomSort()
        props.calculatePoint(clone[index].clicked)

        return
    }

    return (
        <div >
            {data.map(card=>{
                return(
                    <div name={card.name} onClick={handleClick}>
                        <h1>{card.name}</h1>
                    </div>
                )
            })}
        </div>
    )
}
